import React from "react";
import { useState } from "react";
import * as St from "../styles/working.style";

function Working({ box, setBox, removeHandler, reLocateHandler }) {
  const [sortOrder, setSortOrder] = useState("asc");

  const changeSort = (order) => {
    const sortedBox = [...box].sort((a, b) => {
      if (order === "asc") {
        return new Date(a.deadline) - new Date(b.deadline);
      }
      return new Date(b.deadline) - new Date(a.deadline);
    });
    setBox(sortedBox);
  };

  return (
    <div className="working">
      <St.Title>
        <St.TitleSpan>üìù Working </St.TitleSpan>
        <select
          value={sortOrder}
          onChange={(e) => {
            setSortOrder(e.target.value);
            changeSort(e.target.value);
          }}
        >
          <option value="asc">Ïò§Î¶ÑÏ∞®Ïàú</option>
          <option value="desc">ÎÇ¥Î¶ºÏ∞®Ïàú</option>
        </select>
      </St.Title>
      <div className="boxFlex">
        {box
          .filter((item) => item.isDone === false)
          .map(function (item) {
            return (
              <article key={item.id} className="TodoCard">
                <div className="TodoCard-body">
                  <h2>{item.title}</h2>
                  <p>{item.body}</p>
                  <time>
                    {new Date(item.deadline).toLocaleDateString("ko-KR", {
                      year: "numeric",
                      month: "long", // "long"ÏùÑ ÏÇ¨Ïö©ÌïòÎ©¥ Ïõî Ïù¥Î¶ÑÏù¥ Îê®
                      day: "numeric",
                    })}
                    ÍπåÏßÄ
                  </time>
                </div>
                <div className="TodoCard-buttons">
                  <button
                    onClick={() => removeHandler(item.id)}
                    className="removeBtn"
                  >
                    ÏÇ≠Ï†úÌïòÍ∏∞
                  </button>
                  <button
                    onClick={() => reLocateHandler(item.id)}
                    className="completeBtn"
                  >
                    ÏôÑÎ£åÌïòÍ∏∞
                  </button>
                </div>
              </article>
            );
          })}
      </div>
    </div>
  );
}

export default Working;
