          <option value="desc">ÎÇ¥Î¶ºÏ∞®Ïàú</option>
        </select>
      </St.Title>
      <div className="boxFlex">
        {box
          .filter((item) => item.isDone === true)
          .map(function (item) {
            return (
              <article key={item.id} className="TodoCard">
                <div className="TodoCard-body">
                  <h2>{item.title}</h2>
                  <p>{item.body}</p>
                  <time>{item.deadline} ÍπåÏßÄ</time>
                </div>
                <div className="TodoCard-buttons">
                  <button
                    onClick={() => removeHandler(item.id)}
                    className="removeBtn"
                  >
                    ÏÇ≠Ï†úÌïòÍ∏∞
                  </button>
                  <button
                    onClick={() => reLocateHandler(item.id)}
                    className="cancelBtn"
                  >
                    Ï∑®ÏÜåÌïòÍ∏∞
