import "./App.css";

import React, { useState } from "react";

// input Box (ÏÉÅÎã®Ïùò Ï†úÎ™©, ÎÇ¥Ïö©, Ï∂îÍ∞ÄÌïòÍ∏∞) Ïª¥Ìè¨ÎÑåÌä∏
function InputBox(props) {
  return (
    <div className="container-inputTitle">
      <div className="inputTitle">
        <div className="form-floating mb-3">
          <input
            value={props.title}
            onChange={props.inputTitle}
            type="email"
            className="form-control"
            id="floatingInput"
            placeholder="inputTitle"
          />
          <label htmlFor="floatingInput">Ï†úÎ™©</label>
        </div>
        <div className="form-floating mb-3">
          <input
            onKeyPress={props.onCheckEnter}
            value={props.body}
            onChange={props.inputBody}
            type="email"
            className="form-control"
            id="floatingInput"
            placeholder="inputBody"
          />
          <label htmlFor="floatingInput">ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî</label>
        </div>
        <div className="addBtn">
          <button
            onClick={props.addHandler}
            type="button"
            className="btn text-white"
          >
            Ï∂îÍ∞ÄÌïòÍ∏∞
          </button>
        </div>
      </div>
    </div>
  );
}

// Î∂ÄÎ™® Ïª¥Ìè¨ÎÑåÌä∏ ÏãúÏûë
function App() {
  // box, title, body stateÏÑ§Ï†ï
  const [box, setBox] = useState([
    // { Í∏∞Î≥∏Ìòï box Ï∞∏Í≥†Ïö© Ï£ºÏÑù
    //   id: 0,
    //   title: "Ï†úÎ™©",
    //   body: "ÎÇ¥Ïö©",
    //   isDone: false,
    // },
  ]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  // input 'Ï†úÎ™©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // input 'ÎÇ¥Ïö©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // Ï†úÎ™©, ÎÇ¥Ïö© inputÏùÑ ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞ÌôîÌïòÎäî Ìï®Ïàò
  const makeInputEmpty = () => {
    setTitle("");
    setBody("");
  };

  // 'Ï∂îÍ∞ÄÌïòÍ∏∞'Î≤ÑÌäº onclick
  const addHandler = function () {
    // Ï†úÎ™©, ÎÇ¥Ïö© Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ alert
    if (title === "") {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (body === "") {
      alert("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else {
      // Î¶¨Ïä§Ìä∏Îì§ Í∞ÑÏóê id Í∞í Ï§ëÎ≥µÏóÜÎèÑÎ°ù newId ÎßåÎìúÎäî Ï°∞Í±¥Î¨∏
      let newId;
      if (box.length > 0) {
        newId = box[box.length - 1].id + 1;
      } else if (box.length <= 0) {
        newId = 0;
      }

      // inputÏùò value Í∞í Î∞òÏòÅÌïú ÏÉàÎ°úÏö¥ Í∞ùÏ≤¥ ÏÉùÏÑ±
      const newObj = {
        id: newId,
        title: title,
        body: body,
        isDone: false,
      };
      setBox([...box, newObj]);

      // 'Ï∂îÍ∞Ä' ÌÅ¥Î¶≠ ÌõÑ input ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞Ìôî
      makeInputEmpty();
    }
  };

  // 'ÏôÑÎ£å' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ trueÎ°ú, 'Ï∑®ÏÜå' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ falseÎ°ú Î∞îÍæ∏Í∏∞
  const reLocateHandler = function (id) {
    const reLocateBox = box.map(function (item) {
      if (item.id === id) {
        return { ...item, isDone: !item.isDone };
      }
      return item;
    });
    setBox(reLocateBox);
  };

  // ÏÇ≠Ï†úÎ≤ÑÌäº onclick
  const removeHandler = function (id) {
    const removeBox = box.filter(function (item) {
      return item.id !== id;
    });
    setBox(removeBox);
  };

  // enterÌÇ§Î°ú todo Ï∂îÍ∞ÄÌïòÍ∏∞
  const onCheckEnter = (e) => {
    if (e.key === "Enter") {
      addHandler();
    }
  };

  // JSX ÏãúÏûë
  return (
    <div className="container">
      <div className="title">
        <span>‚òÅÔ∏è Welcome to To Do List! ‚õÖÔ∏è</span>
      </div>
      {/* input Î∞ïÏä§ */}
      {/* <div className="container-inputTitle">
        <div className="inputTitle">
          <div className="form-floating mb-3">
            <input
              value={title}
              onChange={inputTitle}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="inputTitle"
            />
            <label htmlFor="floatingInput">Ï†úÎ™©</label>
          </div>
          <div className="form-floating mb-3">
            <input
              onKeyPress={onCheckEnter}
              value={body}
              onChange={inputBody}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="inputBody"
            />
            <label htmlFor="floatingInput">ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî</label>
          </div>
          <div className="addBtn">
            <button
              onClick={addHandler}
              type="button"
              className="btn text-white"
            >
              Ï∂îÍ∞ÄÌïòÍ∏∞
            </button>
          </div>
        </div>
      </div> */}
      <InputBox
        title={title}
        inputTitle={inputTitle}
        onCheckEnter={onCheckEnter}
        body={body}
        inputBody={inputBody}
        addHandler={addHandler}
      />
      <div className="boxContainer">
        <div className="working">
          <span>üìù Working </span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === false)
              .map(function (item) {
                return (
                  <div key={item.id} className="toDoBox">
                    <p className="boxTitle">{item.title}</p>
                    <p className="boxBody">{item.body}</p>
                    <button
                      onClick={() => removeHandler(item.id)}
                      className="removeBtn"
                    >
                      ÏÇ≠Ï†úÌïòÍ∏∞
                    </button>
                    &nbsp;
                    <button
                      onClick={() => reLocateHandler(item.id)}
                      className="completeBtn"
                    >
                      ÏôÑÎ£å
                    </button>
                  </div>
                );
              })}
          </div>
        </div>
        <div className="done">
          <span>üëçüèª Done</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === true)
              .map(function (item) {
                return (
                  <div key={item.id} className="toDoBox">
                    <p className="boxTitle">{item.title}</p>
                    <p className="boxBody">{item.body}</p>
                    <button
                      onClick={() => removeHandler(item.id)}
                      className="removeBtn"
                    >
                      ÏÇ≠Ï†úÌïòÍ∏∞
                    </button>
                    &nbsp;
                    <button
                      onClick={() => reLocateHandler(item.id)}
                      className="cancelBtn"
                    >
                      Ï∑®ÏÜåÌïòÍ∏∞
                    </button>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
