function TodoItem({ item, onUpdate }) {
    const handleChangeDeadline = (newDate) => {
      onUpdate(item.id, { ...item, deadline: newDate });
    };

    return (
      <div>
        <p>{item.text}</p>
        <input
          type="date"
          value={item.deadline}
          onChange={(e) => handleChangeDeadline(e.target.value)}
        />
        <time>{new Date(item.deadline).toLocaleDateString()}</time>
      </div>
    );
  }

  export default TodoItem
