import "./App.css";

import React, { useState } from "react";
import InputBox from "./components/inputBox";
import Header from "./layout/header";
// import TodoItem from "./components/test";

// Î∂ÄÎ™® Ïª¥Ìè¨ÎÑåÌä∏ ÏãúÏûë
function App() {
  // box, title, body stateÏÑ§Ï†ï
  const [box, setBox] = useState([]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [deadline, setDeadline] = useState("");

  // input 'Ï†úÎ™©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // input 'ÎÇ¥Ïö©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // deadline Î≥ÄÍ≤Ω - onChange Ìï®Ïàò
  const changeDeadline = (e) => {
    setDeadline(e.target.value);

    // const newDateObj = new Date(e.target.value);
    // const year = newDateObj.getFullYear();
    // const month = newDateObj.getMonth() + 1;
    // const day = newDateObj.getDate();

    // const afterChangeDateType = `${year}ÎÖÑ ${month}Ïõî ${day}Ïùº`;
    // console.log(afterChangeDateType);

    // setDeadline(afterChangeDateType);
  };

  // Ï†úÎ™©, ÎÇ¥Ïö© inputÏùÑ ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞ÌôîÌïòÎäî Ìï®Ïàò
  const makeInputEmpty = () => {
    setTitle("");
    setBody("");
  };

  // 'Ï∂îÍ∞ÄÌïòÍ∏∞'Î≤ÑÌäº onclick
  const addHandler = function () {
    // Ï†úÎ™©, ÎÇ¥Ïö©, ÎßàÍ∞êÏùº Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ alert
    if (title === "") {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (body === "") {
      alert("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (deadline === "") {
      alert("ÎßàÍ∞êÏùºÏùÑ ÏßÄÏ†ïÌï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else {
      // inputÏùò value Í∞í Î∞òÏòÅÌïú ÏÉàÎ°úÏö¥ Í∞ùÏ≤¥ ÏÉùÏÑ±
      const newObj = {
        id: Date.now(),
        title: title,
        body: body,
        isDone: false,
        deadline: deadline,
      };
      // Îã¨Î†• ÌòïÏãù Î≥ÄÍ≤Ω
      const newDateObj = new Date(deadline);
      const year = newDateObj.getFullYear();
      const month = newDateObj.getMonth() + 1;
      const day = newDateObj.getDate();

      const afterChangeDateType = `${year}ÎÖÑ ${month}Ïõî ${day}Ïùº`;
      console.log(afterChangeDateType);

      setDeadline(afterChangeDateType);

      setBox([...box, newObj]);

      // 'Ï∂îÍ∞Ä' ÌÅ¥Î¶≠ ÌõÑ input ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞Ìôî
      makeInputEmpty();
    }
  };

  // deadline ÌòïÏãù Î≥ÄÍ≤Ω
  // const changeDateType = (e) => {
  //   const newDateObj = new Date(e.target.value);
  //   const year = newDateObj.getFullYear();
  //   const month = newDateObj.getMonth() + 1;
  //   const day = newDateObj.getDate();

  //   const afterChangeDateType = `${year}ÎÖÑ ${month}Ïõî ${day}Ïùº`;
  //   console.log(afterChangeDateType);
  // };

  // 'ÏôÑÎ£å' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ trueÎ°ú, 'Ï∑®ÏÜå' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ falseÎ°ú Î∞îÍæ∏Í∏∞
  const reLocateHandler = function (id) {
    const reLocateBox = box.map(function (item) {
      if (item.id === id) {
        return { ...item, isDone: !item.isDone };
      }
      return item;
    });
    setBox(reLocateBox);
  };

  // ÏÇ≠Ï†úÎ≤ÑÌäº onclick
  const removeHandler = function (id) {
    const removeBox = box.filter(function (item) {
      return item.id !== id;
    });
    setBox(removeBox);
  };

  // enterÌÇ§Î°ú todo Ï∂îÍ∞ÄÌïòÍ∏∞
  const onCheckEnter = (e) => {
    if (e.key === "Enter") {
      addHandler();
    }
  };

  // JSX ÏãúÏûë
  return (
    <div className="container">
      <Header />
      {/* input Î∞ïÏä§ - Ïª¥Ìè¨ÎÑåÌä∏ Î∂ÑÎ¶¨ */}
      <InputBox
        title={title}
        inputTitle={inputTitle}
        onCheckEnter={onCheckEnter}
        body={body}
        inputBody={inputBody}
        addHandler={addHandler}
        deadline={deadline}
        changeDeadline={changeDeadline}
      />
      <div className="boxContainer">
        <div className="working">
          <span>üìù Working </span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === false)
              .map(function (item) {
                return (
                  <article key={item.id} className="TodoCard">
                    <div className="TodoCard-body">
                      <h2>{item.title}</h2>
                      <p>{item.body}</p>
                      <time>{item.deadline}</time>
                    </div>
                    <div className="TodoCard-buttons">
                      <button
                        onClick={() => removeHandler(item.id)}
                        className="removeBtn"
                      >
                        ÏÇ≠Ï†úÌïòÍ∏∞
                      </button>
                      <button
                        onClick={() => reLocateHandler(item.id)}
                        className="completeBtn"
                      >
                        ÏôÑÎ£åÌïòÍ∏∞
                      </button>
                    </div>
                  </article>
                );
              })}
          </div>
        </div>
        <div className="done">
          <span>üëçüèª Done</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === true)
              .map(function (item) {
                return (
                  <article key={item.id} className="TodoCard">
                    <div className="TodoCard-body">
                      <h2>{item.title}</h2>
                      <p>{item.body}</p>
                      <time>{item.deadline}</time>
                    </div>
                    <div className="TodoCard-buttons">
                      <button
                        onClick={() => removeHandler(item.id)}
                        className="removeBtn"
                      >
                        ÏÇ≠Ï†úÌïòÍ∏∞
                      </button>
                      <button
                        onClick={() => reLocateHandler(item.id)}
                        className="cancelBtn"
                      >
                        Ï∑®ÏÜåÌïòÍ∏∞
                      </button>
                    </div>
                  </article>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
