// input Box 컴포넌트 (상단의 제목, 내용, 추가하기)
function InputBox({}) {
    // input '제목' 입력한 value - onchange 함수
    const inputTitle = function (e) {
      setTitle(e.target.value);
    };

    // input '내용' 입력한 value - onchange 함수
    const inputBody = function (e) {
      setBody(e.target.value);
    };

    // deadline 변경 - onChange 함수
    const changeDeadline = (e) => {
      setDeadline(e.target.value);
    };

    // 제목, 내용 input을 빈칸으로 초기화하는 함수
    const makeInputEmpty = () => {
      setTitle("");
      setBody("");
    };

    // '추가하기'버튼 onclick
    const addHandler = function () {
      // 제목, 내용, 마감일 유효성 검사 alert
      if (title === "") {
        alert("제목을 입력해주세요.");
        makeInputEmpty();
      } else if (body === "") {
        alert("내용을 입력해주세요.");
        makeInputEmpty();
      } else if (deadline === "") {
        alert("마감일을 지정해주세요.");
        makeInputEmpty();
      } else {
        // input의 value 값 반영한 새로운 객체 생성
        const newDateObj = new Date(deadline);
        const afterChangeDateType = newDateObj.toLocaleDateString("ko-KR", {
          year: "numeric",
          month: "long", // "long"을 사용하면 월 이름이 됩니다.
          day: "numeric",
        });

        setDeadline(afterChangeDateType);

        setBox((box) => [
          ...box,
          {
            id: Date.now(),
            title: title,
            body: body,
            isDone: false,
            deadline: afterChangeDateType,
          },
        ]);

        // '추가' 클릭 후 input 빈칸으로 초기화
        makeInputEmpty();
      }
    };

    return (
      <div className="container-inputTitle">
        <div className="inputTitle">
          <div className="form-floating mb-3">
            <input
              value={title}
              onChange={inputTitle}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="inputTitle"
            />
            <label htmlFor="floatingInput">제목</label>
          </div>
          <div className="form-floating mb-3">
            <input
              onKeyPress={onCheckEnter}
              value={body}
              onChange={inputBody}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="inputBody"
            />
            <label htmlFor="floatingInput">내용을 입력하세요</label>
          </div>
          <time className="inputBox-time">
            <label for="start">마감일 :</label>
            <input type="date" id="start" name="trip-start" min="2024-01-01" max="2026-12-31" 
            value={deadline}
            onChange={changeDeadline}
            />
          </time>
          <div className="addBtn">
            <button
              onClick={addHandler}
              type="button"
              className="btn text-white"
            >
              추가하기
            </button>
          </div>
        </div>
      </div>
    );
  }

  export default InputBox;
