import "./App.css";

import React, { useState } from "react";

function App() {
  // box, title, body state설정
  const [box, setBox] = useState([
    // {
    //   id: 1,
    //   title: "제목을 입력하세요",
    //   body: "내용을 입력하세요",
    //   isDone: false,
    // },
  ]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  // '제목' 입력한 value - onchange 함수
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // '내용' 입력한 value - onchange 함수
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // 추가 onclick
  const addHandler = function () {
    const newObj = {
      id: box.length + 1,
      title: title,
      body: body,
      isDone: false,
    };
    setBox([...box, newObj]);
    // 클릭 후 input 빈칸으로 초기화
    setTitle("");
    setBody("");
  };

  // '완료' 클릭하면 isDone을 true로 바꿔줘
  const completeHandler = function () {};

  // '취소' 클릭하면 isDone을 false로 바꿔줘

  // JSX 시작
  return (
    <div className="container">
      <div className="title">
        <span>My Todo List</span>
        <span>React</span>
      </div>
      <div className="inputTitle">
        <div>
          제목 :&nbsp;
          <input value={title} onChange={inputTitle} />
          &nbsp; 내용 :&nbsp;
          <input value={body} onChange={inputBody} /> &nbsp;
        </div>
        <div className="addBtn">
          <button onClick={addHandler}>추가하기</button>
        </div>
      </div>
      <div className="boxContainer">
        <div className="working">
          <span>Working..🔥</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === false)
              .map(function (item) {
                // console.log(item);
                return (
                  <div key={item.id} className="workingBox">
                    <span style={{ fontSize: "20px" }}>{item.title}</span>
                    <p style={{ fontSize: "15px" }}>{item.body}</p>
                    <button className="removeBtn">삭제하기</button>
                    &nbsp;
                    <button className="completeBtn">완료</button>
                  </div>
                );
              })}
          </div>
        </div>
        <div className="done">
          <span>Done..🥳</span>
          <div className="boxFlex">
            {/* {box
              .filter(function (item) {
                return item.isDone === true;
              })
              .map(function (item) {
                return (
                  <div key={item.id} className="workingBox">
                    <span style={{ fontSize: "20px" }}>{item.title}</span>
                    <p style={{ fontSize: "15px" }}>{item.body}</p>
                    <button className="removeBtn">삭제하기</button>
                    &nbsp;
                    <button className="completeBtn">완료</button>
                  </div>
                );
              })} */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
