  // 'Ï†úÎ™©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // 'ÎÇ¥Ïö©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // Ï∂îÍ∞Ä onclick
  const addHandler = function () {
    const newObj = {
      id: box.length + 1,
      title: title,
      body: body,
      isDone: false,
    };
    setBox([...box, newObj]);
    // ÌÅ¥Î¶≠ ÌõÑ input ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞Ìôî
