  // Ï†úÎ™©, ÎÇ¥Ïö© inputÏùÑ ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞ÌôîÌïòÎäî Ìï®Ïàò
  const makeInputEmpty = () => {
    setTitle("");
    setBody("");
  };

  // 'Ï∂îÍ∞ÄÌïòÍ∏∞'Î≤ÑÌäº onclick
  const addHandler = function () {
    // Ï†úÎ™©, ÎÇ¥Ïö©, ÎßàÍ∞êÏùº Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ alert
    if (title === "") {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (body === "") {
      alert("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (deadline === "") {
      alert("ÎßàÍ∞êÏùºÏùÑ ÏßÄÏ†ïÌï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else {
      // inputÏùò value Í∞í Î∞òÏòÅÌïú ÏÉàÎ°úÏö¥ Í∞ùÏ≤¥ ÏÉùÏÑ±
      const newDateObj = new Date(deadline);
      const afterChangeDateType = newDateObj.toLocaleDateString("ko-KR", {
        year: "numeric",
        month: "long", // "long"ÏùÑ ÏÇ¨Ïö©ÌïòÎ©¥ Ïõî Ïù¥Î¶ÑÏù¥ Îê©ÎãàÎã§.
        day: "numeric",
      });

      setDeadline(afterChangeDateType);

      setBox((box) => [
        ...box,
        {
          id: Date.now(),
          title: title,
          body: body,
          isDone: false,
          deadline: afterChangeDateType,
        },
      ]);

      // 'Ï∂îÍ∞Ä' ÌÅ¥Î¶≠ ÌõÑ input ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞Ìôî
      makeInputEmpty();
    }
  };

  // 'ÏôÑÎ£å' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ trueÎ°ú, 'Ï∑®ÏÜå' ÌÅ¥Î¶≠Ïãú isDoneÏùÑ falseÎ°ú Î∞îÍæ∏Í∏∞
  const reLocateHandler = function (id) {
    const reLocateBox = box.map(function (item) {
      if (item.id === id) {
        return { ...item, isDone: !item.isDone };
      }
      return item;
    });
    setBox(reLocateBox);
  };

  // ÏÇ≠Ï†úÎ≤ÑÌäº onclick
  const removeHandler = function (id) {
    const removeBox = box.filter(function (item) {
      return item.id !== id;
    });
    setBox(removeBox);
  };

  // enterÌÇ§Î°ú todo Ï∂îÍ∞ÄÌïòÍ∏∞
  const onCheckEnter = (e) => {
    if (e.key === "Enter") {
      addHandler();
    }
  };

  // JSX ÏãúÏûë
  return (
    <div className="container">
      <Header />
      {/* input Î∞ïÏä§ - Ïª¥Ìè¨ÎÑåÌä∏ Î∂ÑÎ¶¨ */}
      <InputBox
        title={title}
        inputTitle={inputTitle}
        onCheckEnter={onCheckEnter}
        body={body}
        inputBody={inputBody}
        addHandler={addHandler}
        deadline={deadline}
        changeDeadline={changeDeadline}
      />
      <div className="boxContainer">
        <Working
          box={box}
          removeHandler={removeHandler}
          reLocateHandler={reLocateHandler}
        />
        {/* <div className="done">
          <span>üëçüèª Done</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === true)
              .map(function (item) {
                return (
                  <article key={item.id} className="TodoCard">
                    <div className="TodoCard-body">
                      <h2>{item.title}</h2>
                      <p>{item.body}</p>
                      <time>{item.deadline} ÍπåÏßÄ</time>
                    </div>
                    <div className="TodoCard-buttons">
                      <button
                        onClick={() => removeHandler(item.id)}
                        className="removeBtn"
                      >
                        ÏÇ≠Ï†úÌïòÍ∏∞
                      </button>
                      <button
                        onClick={() => reLocateHandler(item.id)}
                        className="cancelBtn"
                      >
                        Ï∑®ÏÜåÌïòÍ∏∞
                      </button>
                    </div>
                  </article>
                );
              })}
          </div>
        </div> */}
        <Done
          box={box}
          removeHandler={removeHandler}
          reLocateHandler={reLocateHandler}
        />
      </div>
    </div>
  );
}

