import "./App.css";

import React, { useState } from "react";

function App() {
  // box, title, body stateÏÑ§Ï†ï
  const [box, setBox] = useState([
    {
      id: 1,
      title: "Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî",
      body: "ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî",
      isDone: false,
    },
  ]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  // 'Ï∂îÍ∞Ä'Î≤ÑÌäºÏùò onclick Ìï®Ïàò
  const addClickHandler = () => {
    const newBoxObj = {
      id: box.length + 1,
      title: title,
      body: body,
      isDone: false,
    };
    setBox([...box, newBoxObj]);
    // inputÏóê value={title}, body Ï†ïÌï¥Ï†∏ ÏûàÏúºÎãàÍπå, ÌÅ¥Î¶≠ ÌõÑÏóê title, bodyÎ•º ''Î°ú Î∞îÍøîÏ§ÄÎã§Îäî Î™ÖÎ†π set -> ÌÅ¥Î¶≠ ÌõÑ inputÎûÄÏù¥ ÎπàÍ∞íÏúºÎ°ú Î∞îÎÄú
    setTitle("");
    setBody("");
  };

  // 'Ï†úÎ™©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // 'ÎÇ¥Ïö©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // JSX ÏãúÏûë
  return (
    <div className="container">
      <div className="title">
        <span>My Todo List</span>
        <span>React</span>
      </div>
      <div className="inputTitle">
        <div>
          Ï†úÎ™© :&nbsp;
          <input value={title} onChange={inputTitle} />
          &nbsp; ÎÇ¥Ïö© :&nbsp;
          <input value={body} onChange={inputBody} /> &nbsp;
        </div>
        <div className="addBtn">
          <button onClick={addClickHandler}>Ï∂îÍ∞ÄÌïòÍ∏∞</button>
        </div>
      </div>
      <div className="boxContainer">
        <div className="working">
          <span>Working..üî•</span>
          <div className="boxFlex">
            {box.map(function (item) {
              return (
                <div key={item.id} className="workingBox">
                  <span style={{ fontSize: "20px" }}>{item.title}</span>
                  <p style={{ fontSize: "15px" }}>{item.body}</p>
                  <button className="removeBtn">ÏÇ≠Ï†úÌïòÍ∏∞</button>&nbsp;
                  <button className="completeBtn">ÏôÑÎ£å</button>
                </div>
              );
            })}
          </div>
        </div>
        <div className="done">
          <span style={{ marginLeft: "10px" }}>Done..ü•≥</span>
          {/* <div className="workingBox">
          <span style={{ fontSize: "20px" }}>Ï†úÎ™©</span>
          <p style={{ fontSize: "15px" }}>ÎÇ¥Ïö©</p>
          <button className="removeBtn">ÏÇ≠Ï†úÌïòÍ∏∞</button>
          <button className="completeBtn">ÏôÑÎ£å</button>
        </div> */}
        </div>
      </div>
    </div>
  );
}

export default App;
