import "./App.css";

import React, { useState } from "react";

function App() {
  // box, title, body stateÏÑ§Ï†ï
  const [box, setBox] = useState([
    // { Í∏∞Î≥∏Ìòï box Ï∞∏Í≥†Ïö© Ï£ºÏÑù
    //   id: 0,
    //   title: "Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî",
    //   body: "ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî",
    //   isDone: false,
    // },
  ]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  // input 'Ï†úÎ™©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputTitle = function (e) {
    setTitle(e.target.value);
  };

  // input 'ÎÇ¥Ïö©' ÏûÖÎ†•Ìïú value - onchange Ìï®Ïàò
  const inputBody = function (e) {
    setBody(e.target.value);
  };

  // Ï†úÎ™©, ÎÇ¥Ïö© ÎπàÏπ∏ÏúºÎ°ú ÎßåÎìúÎäî Ìï®Ïàò
  const makeInputEmpty = () => {
    setTitle("");
    setBody("");
  };

  // Ï∂îÍ∞Ä onclick
  const addHandler = function () {
    // Ï†úÎ™©, ÎÇ¥Ïö© Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ alert
    if (title === "") {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else if (body === "") {
      alert("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      makeInputEmpty();
    } else {
      // id Í∞í Ï§ëÎ≥µÏóÜÍ≤å ÌïòÍ∏∞ ÏúÑÌï¥ newId ÎßåÎìúÎäî Ï°∞Í±¥Î¨∏
      let newId;
      if (box.length > 0) {
        newId = box[box.length - 1].id + 1;
      } else if (box.length <= 0) {
        newId = 0;
      }

      // inputÏùò value Í∞í Î∞òÏòÅÌïú ÏÉàÎ°úÏö¥ Í∞ùÏ≤¥ ÏÉùÏÑ±
      const newObj = {
        id: newId,
        title: title,
        body: body,
        isDone: false,
      };
      setBox([...box, newObj]);

      // ÌÅ¥Î¶≠ ÌõÑ input ÎπàÏπ∏ÏúºÎ°ú Ï¥àÍ∏∞Ìôî
      setTitle("");
      setBody("");
    }
  };

  // 'ÏôÑÎ£å' ÌÅ¥Î¶≠ÌïòÎ©¥ isDoneÏùÑ trueÎ°ú Î∞îÍøîÏ§ò
  const completeHandler = function (id) {
    const trueBox = box.map(function (item) {
      if (item.id === id) {
        return { ...item, isDone: true };
      }
      return item;
    });
    setBox(trueBox);
  };

  // 'Ï∑®ÏÜå' ÌÅ¥Î¶≠ÌïòÎ©¥ isDoneÏùÑ falseÎ°ú Î∞îÍøîÏ§ò
  const cancelHandler = function (id) {
    const falseBox = box.map(function (item) {
      if (item.id === id) {
        return { ...item, isDone: false };
      }
      return item;
    });
    setBox(falseBox);
  };

  // ÏÇ≠Ï†úÎ≤ÑÌäº onclick
  const removeHandler = function (id) {
    const removeBox = box.filter(function (item) {
      return item.id !== id;
    });
    setBox(removeBox);
  };

  // enterÌÇ§Î°ú todo Ï∂îÍ∞ÄÌïòÍ∏∞
  const onCheckEnter = (e) => {
    if (e.key === "Enter") {
      addHandler();
    }
  };

  // JSX ÏãúÏûë
  return (
    <div className="container">
      <div className="title">
        <span>‚òÅÔ∏è Welcome to To Do List! ‚õÖÔ∏è</span>
      </div>
      <div className="container-inputTitle">
        <div className="inputTitle">
          <div className="form-floating mb-3">
            <input
              value={title}
              onChange={inputTitle}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="name@example.com"
            />
            <label htmlFor="floatingInput">Ï†úÎ™©</label>
          </div>
          <div className="form-floating mb-3">
            <input
              onKeyPress={onCheckEnter}
              value={body}
              onChange={inputBody}
              type="email"
              className="form-control"
              id="floatingInput"
              placeholder="name@example.com"
            />
            <label htmlFor="floatingInput">ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî</label>
          </div>
          <div className="addBtn">
            <button
              onClick={addHandler}
              type="button"
              className="btn text-white"
            >
              Ï∂îÍ∞ÄÌïòÍ∏∞
            </button>
          </div>
        </div>
      </div>
      <div className="boxContainer">
        <div className="working">
          <span>üìù To Do List</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === false)
              .map(function (item) {
                return (
                  <div key={item.id} className="workingBox">
                    <p className="boxTitle">{item.title}</p>
                    <p className="boxBody">{item.body}</p>
                    <button
                      onClick={() => removeHandler(item.id)}
                      className="removeBtn"
                    >
                      ÏÇ≠Ï†úÌïòÍ∏∞
                    </button>
                    &nbsp;
                    <button
                      onClick={() => completeHandler(item.id)}
                      className="completeBtn"
                    >
                      ÏôÑÎ£å
                    </button>
                  </div>
                );
              })}
          </div>
        </div>
        <div className="done">
          <span>üëçüèª Done</span>
          <div className="boxFlex">
            {box
              .filter((item) => item.isDone === true)
              .map(function (item) {
                return (
                  <div key={item.id} className="workingBox">
                    <p className="boxTitle">{item.title}</p>
                    <p className="boxBody">{item.body}</p>
                    <button
                      onClick={() => removeHandler(item.id)}
                      className="removeBtn"
                    >
                      ÏÇ≠Ï†úÌïòÍ∏∞
                    </button>
                    &nbsp;
                    <button
                      onClick={() => cancelHandler(item.id)}
                      className="cancelBtn"
                    >
                      Ï∑®ÏÜåÌïòÍ∏∞
                    </button>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
